export class TileMap extends View
{
	constructor(args,parent)
	{
		super(args,parent);

		this.heightMask = null;

		const image = new Image();

		this.mapData = mapData;

		this.ready = new Promise(accept => {
			image.addEventListener('load', event => {

				this.width  = image.width;
				this.height = image.height;

				const heightMask = new Tag('<canvas>');

				heightMask.width  = image.width;
				heightMask.height = image.height;

				heightMask.getContext('2d').drawImage(
					image, 0, 0, this.width, this.height
				);

				this.heightMask = heightMask;

				accept();
			});

			image.src = '/Sonic/testTiles2.png';
		});
	}

	coordsToTile(x, y)
	{
		return [Math.floor(x / this.blockSize) , Math.floor(y / this.blockSize)];
	}

	getTileNumber(x, y)
	{
		this.tileNoCache = this.tileNoCache || {};

		this.tileNoCache[x] = this.tileNoCache[x] || {};

		if(this.tileNoCache[x][y] !== undefined)
		{
			return this.tileNoCache[x][y];
		}

		if(x >= this.mapData.width || y >= this.mapData.height
			|| x < 0 || y < 0
		){
			return this.tileNoCache[x][y] = false;
		}

		const tileIndex = (y * this.mapData.width) + x;

		if(this.mapData.layers[0].data[tileIndex] !== undefined)
		{
			return this.tileNoCache[x][y] = this.mapData.layers[0].data[tileIndex] - 1;
		}

		return this.tileNoCache[x][y] = false;
	}

	getTile(tileNumber)
	{
		let x = 0;
		let y = 0;

		if(tileNumber)
		{
			const blocksWide = Math.ceil(this.width / 32);

			x = tileNumber % blocksWide;
			y = Math.floor(tileNumber/blocksWide);
		}

		return [x,y];
	}

	getSolid(tileNumber, xInput, yInput)
	{
		if(tileNumber === 0)
		{
			return false;
		}

		if(tileNumber === 1)
		{
			return true;
		}

		const tilePos = this.getTile(tileNumber).map(coord => {
			return coord * this.blockSize
		});

		const x = Math.floor(Number(xInput) % this.blockSize);
		const y = Math.floor(Number(yInput) % this.blockSize);

		if(
			this.heightMaskCache !== undefined
			&& this.heightMaskCache[tileNumber] !== undefined
			&& this.heightMaskCache[tileNumber][x] !== undefined
			&& this.heightMaskCache[tileNumber][x][y] !== undefined
		){
			return this.heightMaskCache[tileNumber][x][y];
		}

		if(this.heightMask)
		{
			const xPixel = tilePos[0] + x;
			const yPixel = tilePos[1] + y;

			if(!this.heightMaskCache)
			{
				this.heightMaskCache = {};
			}

			if(!this.heightMaskCache[tileNumber])
			{
				this.heightMaskCache[tileNumber] = {};
			}

			if(!this.heightMaskCache[tileNumber][x])
			{
				this.heightMaskCache[tileNumber][x] = {};
			}

			this.heightMaskCache[tileNumber][x][y] = false;

			const pixel = this.heightMask.getContext('2d').getImageData(
				xPixel, yPixel, 1, 1
			).data;

			if(pixel[0] == 0)
			{
				this.heightMaskCache[tileNumber][x][y] = true;
			}

			return this.heightMaskCache[tileNumber][x][y];
		}

		return false;
	}

	get blockSize()
	{
		return this.mapData.tilewidth;
	}
}
